import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'

const UserTasks = () => {
  const  Uri='http://localhost:4000';
  const [userTasks,setUserTasks]=useState([])

  const {sId}=useParams()
  
  useEffect(()=>{
      axios.post(Uri+'/userTasks',{sId})
      .then(dbres=>{
        console.log('userTasks fetched',dbres)
        setUserTasks(dbres.data.dbres)
      })
      .catch(err=>{
        console.log('could not fetch any tasks',err)
      })
     },[])

  return (
    <div>
      UserTasks
      {userTasks.map((task,ind)=>(
        <div key={ind} className="d-grid gap-3 border border-2 border-tertiary">
         <div className=' d-flex justify-content-space-around '> <span><p>{ind+1} -- {task.task}</p></span></div> 
            
       </div>))}
     
    </div>
  )
}

export default UserTasks
