import axios from 'axios';
import React, { useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { setTasks } from '../store/taskSlice';

const UserTasks = () => {
  const Uri = 'http://localhost:4000';
  const dispatch = useDispatch();
  const { allTasks } = useSelector(state => state.tasks);
  const { sId } = useParams();

  useEffect(() => {
    axios.post(Uri + '/userTasks', { sId })
      .then(dbres => {
        console.log('userTasks fetched', dbres);
        dispatch(setTasks(dbres.data.dbres));
      })
      .catch(err => {
        console.log('could not fetch any tasks', err);
      });
  }, [dispatch, sId]);

  return (
    <div>
      <h3 className='mx-3 beta border border-round border-3 border-warning'>UserTasks</h3>
      {allTasks.map((task, ind) => (
        <div key={ind} className="d-flex border border-2 border-tertiary justify-content-between">
          <div className='ms-1'><span>{ind + 1}. </span></div>
          <p className='ms-5'> - {task.task}</p>
          <div className='position absolute end-0'>
            <button className='btn btn-primary my-1 mx-1'>Edit</button>
            <button className='btn btn-danger my-1 mx-1'>Delete</button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default UserTasks;
