import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { Link, Outlet, useNavigate } from 'react-router-dom'


const Profile = () => {
  const Uri='http://localhost:4000'
  const navigate=useNavigate();
 const [userData,setUserData]=useState({})
  useEffect(()=>{
    const token=localStorage.getItem('user')
    if(token==="undefined"){
      navigate('/')
    }
    axios({
      method:'post',
      url:Uri+"/user",
      headers:{
        Authorization:"Bearer "+token
      }
    })
    .then(dbres=>{
      setUserData(dbres.data.dbres)
      
    })
    .catch(err=>{
      console.log("user does not exist",err)
      navigate("/")
    })
    })


  return (
    <div className='d-flex flex-column'>
      <h3 className='text-white bg-dark mt-2 mb-4'>welcome {userData.name} </h3>
      <div className="input-group input-group-lg">
  <span className="input-group-text" id="inputGroup-sizing-lg">Enter a task</span>
  <input type="text" className="form-control" aria-label="Sizing example input" aria-describedby="inputGroup-sizing-lg"/>
    <button className='btn btn-success'> Add</button>
</div>
      <Link to='/profile/userTasks'><button className='btn btn-success mt-3 ms-3 mb-2'>UserTasks</button></Link>
      <div className='container'>
        <Outlet></Outlet>
      </div>
    </div>
  )
}

export default Profile
