import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'

const UserTasks = () => {
  const  Uri='http://localhost:4000';
  const [userTasks,setUserTasks]=useState([])

  const {sId}=useParams()
  
  useEffect(()=>{
      axios.post(Uri+'/userTasks',{sId})
      .then(dbres=>{
        console.log('userTasks fetched',dbres)
        setUserTasks(dbres.data.dbres)
      })
      .catch(err=>{
        console.log('could not fetch any tasks',err)
      })
     },[])
   
  return (
    <div>
      <h3 className='beta border border-round border-3 border-warning'>UserTasks</h3>
      {userTasks.map((task,ind)=>(
        <div key={ind} className="d-flex  border border-2 border-tertiary justify-content-between">

        <div className='w-25'><span >{ind+1}-- </span></div>
        <p>  {task.task}</p>
        <div className='position absolute end-0'>
        <button className='btn btn-primary me-3'>Edit</button>
        <button className='btn btn-danger'>Delete</button>
        </div>

            
       </div>))}
     
    </div>
  )
}

export default UserTasks
