import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { Link, Outlet, useNavigate } from 'react-router-dom'


const Profile = () => {
  const Uri='http://localhost:4000'
  const navigate=useNavigate();
 const [userData,setUserData]=useState("")
 const [task,setTask]=useState("")
  useEffect(()=>{
    const token=localStorage.getItem('user')
    if(token==="undefined"){
      navigate('/')
    }
    axios({
      method:'post',
      url:Uri+"/user",
      headers:{
        Authorization:"Bearer "+token
      }
    })
    .then(dbres=>{
      setUserData(dbres.data.dbres)
      
    })
    .catch(err=>{
      console.log("user does not exist",err)
      navigate("/")
    })
    })
    const changeHandler=(event)=>{
      event.preventDefault()
     const{name,value}=event.target
      setTask(value)
        
  }

    const submitHandler=(event)=>{
      event.preventDefault()
      if(!(task===null)){
        axios.post(Uri+"/addTask",{task,userEmail:userData.email})
        .then(res=>{
          console.log("task saved" ,res)
        })
        .catch(err=>{
          console.log("could not save the task",err)
        })
      }
      
  }

  return (
    <div className='d-flex flex-column container'>
      <h3 className='border border-round border-1 text-white bg-dark mt-2 mb-4'>welcome {userData.name} </h3>
      <form onSubmit={submitHandler}>
      <div className="input-group input-group-lg border ">
  <span className="input-group-text" id="inputGroup-sizing-lg">Enter a task</span>
  <input type="text" className="form-control me-3" name='task' onChange={changeHandler} aria-label="Sizing example input" aria-describedby="inputGroup-sizing-lg"/>
    <button type='submit' className='btn btn-success'> Add</button>
</div>
      </form>
      <Link to='/profile/userTasks'><button className='btn btn-success mt-3 ms-3 mb-2'>UserTasks</button></Link>
      <div className='container'>
        <div className="container">
        <Outlet></Outlet>
        </div>
      </div>
    </div>
  )
}

export default Profile
