import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'

const Edit = () => {
   const [task,setTask]=useState("")
    const {tid}=useParams();
    const Uri='http://localhost:4000';
  
    useEffect(()=>{
      axios.post(Uri+'/getEditTask',{tid})
      .then(dbres=>{
        setTask(dbres.data.dbres.task)
      })
      .catch(err=>{
        console.log('could not find the task to edit',err)
      })
    }
  ,[])

  
  const changeHandler=(event)=>{
    event.preventDefault()
   const{name,value}=event.target
    setTask(value)
      
}

 const submitHandler = (event) => {
    event.preventDefault();
    if (task) {
      axios.put(Uri + "/updateTask", {task})
        .then(res => {
          console.log("task modified and saved", res);
          // Refetch all tasks after adding
          // return axios.post(Uri + "/userTasks", { sId: userData._id });
        })
        
        .catch(err => {
          console.log("error in saving or fetching tasks", err);
        });
    }
  };
  return (
    <div>
          <div className='d-flex flex-column container'>
                <form onSubmit={submitHandler}>
                          <div className="input-group input-group-lg border ">
                                  <h1>Edit the task</h1>
                                {/* <span className="input-group-text" id="inputGroup-sizing-lg"></span> */}
                                <input type="text" className="form-control me-3" name='task' value={task} onChange={changeHandler} aria-label="Sizing example input" aria-describedby="inputGroup-sizing-lg"/>
                                <button type='submit' className='btn btn-success'> update</button>
                          </div>
                </form>
        
        </div>
    </div>
  )
}

export default Edit
